	session_start();
	include_once("../includes.php");
	include_all();

	if ($_SESSION["user_level"] != 2)
		header("Location: ".PROJECT);

	function ft_adm_button()
	{
		$sql = "SHOW DATABASES LIKE 'db_tbouder';";
		$count = ft_exec_sql("rowCount", $sql);
		echo "<div class='adm_container'>";
		if ($count === 0)
		{
			echo "<a href='".PROJECT_CONFIG."setup.php'><div class='adm_button'>Create Database</div></a>";
			echo "<a href='".PROJECT_ADMIN."sc_clear_db.php' class='disable_link'><div class='adm_button adm_red'>Reset Database</div></a>";
		}
		else
		{
			echo "<a href='".PROJECT_CONFIG."setup.php' class='disable_link'><div class='adm_button adm_red'>Create Database</div></a>";
			echo "<a href='".PROJECT_ADMIN."sc_clear_db.php'><div class='adm_button'>Reset Database</div></a>";
		}
		echo "</div>";
	}

	function ft_adm_users()
	{
		$sql = "SELECT * FROM db_tbouder.users;";
		$users = ft_exec_sql("fetchAll", $sql);
		echo "<table class='adm_users_table'>";
			echo "<tr>";
				echo "<th>Login</th>";
				echo "<th>Email adresse</th>";
				echo "<th>User level</th>";
				echo "<th>Is activ</th>";
				echo "<th>Delete</th>";
			echo "</tr>";
		foreach ($users as $key => $value)
		{
				echo "<tr>";
					echo "<td>".$value['login']."</td>";
					echo "<td>".$value['email']."</td>";
					echo "<td class='adm_plusminus_container'>";
						echo "<img alt='minus' class='adm_picture_minus' onclick='ft_adm_ajax_minus(\"user_level\", \"".$value['login']."\")' <img src='data:image/svg+xml;utf8;base64,' />";

						echo $value['user_level'];

						echo "<img alt='plus' class='adm_picture_plus' onclick='ft_adm_ajax_plus(\"user_level\", \"".$value['login']."\")' <img src='data:image/svg+xml;utf8;base64,' />";
					echo "</td>";

					echo "<td class='adm_plusminus_container'>";
						echo "<img alt='minus' class='adm_picture_minus' onclick='ft_adm_ajax_minus(\"activ\", \"".$value['login']."\")' src='data:image/svg+xml;utf8;base64,' />";

						echo $value['activ'];

						echo "<img alt='plus' class='adm_picture_plus' onclick='ft_adm_ajax_plus(\"activ\", \"".$value['login']."\")' src='data:image/svg+xml;utf8;base64,' />";
					echo "</td>";



					echo "<td class='adm_picture_container'>";
						echo "<img alt='delete' class='adm_picture_remove' onclick='ft_adm_ajax_delete_user(\"".$value['login']."\")' src='data:image/svg+xml;utf8;base64,'/>";
					echo "</td>";
				echo "</tr>";
		}
		echo "</table>";
	}

	ft_adm_button();
	ft_adm_users();
?>

